INSERT INTO `product` (`id`, `category`, `eng_title`, `rus_title`, `slug`, `tag`, `preview`, `vantage`, `card_text`, `application`, `ingredients`, `active`, `in_stock`, `line`, `thumb_id`, `sort_order`) VALUES
(1, 1, 'No 10 Evening Primrose Oil Shampoo', 'Шампунь № 10 Масло Вечерней Примулы', '10-evening-primrose-oil-shampoo', '№10', 'Предназначен для улучшения структуры шерсти, питает сухую кожу,добавляет блеск, способствует исцелению, облегчает расчесывание.Подходит для использования каждый день. Содержит 1% масла Вечерней Примулы. Богат жирными кислотами Омега 6. Масло&nbsp;Вечерней Примулы творит чудеса на коже и шерсти.добавляет блеск, способствует исцелению, облегчает расчесывание.Подходит для использования каждый день.', '', '<p>Этот шампунь для собак уникален тем, что удалось растворить масло Вечерней примулы в воде и при этом довести концентрацию до 1%.</p>\r\n<p>Таким образом, шампунь, вымывая естественные масла из шерсти, заменит их маслом Вечерней Примулы, необходимым для здоровой и блестящей шерсти.</p>\r\n<p>Масло Вечерней Примулы является успокаивающим по своей природе и помогает в выздоровлении сухой и шелушащейся кожи.</p>\r\n<p>Если шерсть имеет нормальное, правильное содержание масел, то она будет хорошо противостоять попаданию и закреплению частичек грязи, отталкивая их, будет меньше пачкаться и не будет страдать от внешних атмосферных и тепловых воздействий, сохраняя надолго красивый, здоровый вид.</p>', 'Хорошо намочите шерсть, нанесите шампунь, хорошо пропитайте, помогая руками тщательно распределиться по всей шерсти. Оставьте шампунь на 2-5 минут, а затем тщательно смойте. Используйте кондиционер №50 или №51, затем промойте и просушите. Если шерсть Вашей собаки очень грязная, то может потребоваться повторное мытье.', 'мягкая вода, лаурет сульфат натрия, диэтаноламин кокамид, сополимер акрилата, полиэтиленгликоль-3 дистеарат, масло энотеры двухлетней (энотеры), ароматизирующее вещество, ДМДМ гидантоин, лодопропинил бутилкарбамат, лимонная кислота, хлорид натрия', 1, 1, 1, NULL, 5),
(2, 1, '№ 12 High Grade Evening Primrose Oil Shampoo', 'Шампунь № 12 Масло Вечерней Примулы Тройная Сила', '12-high-grade-evening-primrose-oil-shampoo', '№12', 'Данный шампунь содержит в три раза больше  масла Вечерней Примулы, чем шампунь №10. Этот лечебный, питательный продукт, заботящийся о шерсти собаки, позволяет избавиться от зуда, шелушащейся кожи и восстановить здоровую, естественную красоту волос. Богат жирными кислотами Омега 6.', '', 'Данный шампунь содержит в три раза больше  масла Вечерней Примулы, чем шампунь №10. Этот лечебный, питательный продукт, заботящийся о шерсти собаки, позволяет избавиться от зуда, шелушащейся кожи и восстановить здоровую, естественную красоту волос. Богат жирными кислотами Омега 6. Активным ингредиентом, делающим этот продукт уникальным, является эффективное масло Вечерней Примулы, предписанное в клинических исследованиях для исправления сухой кожи, не закупоривающее, но стимулирующее структурные изменения в коже. Масло Вечерней Примулы богато гамма - линолевой кислотой, которая была в ходе научных исследований определена как наиболее важная жирная кислота в балансировании простагландинов (регулирующая молекула, влияющая на воспалительные процессы) у собак с аллергическими признаками. Приводит к улучшению гидратации кожи, защите кожи от внешних воздействий и помогает при воспалительных процессах. ', 'Хорошо намочите шерсть, нанесите шампунь, хорошо пропитайте помогая руками тщательно распределиться по всей шерсти. Оставьте шампунь на 2-5 минут, а затем ее необходимо основательно смыть. Используйте кондиционер Formula №50 или №51, затем промойте и просушите. Если шерсть Вашей собаки очень грязная, то может потребоваться повторное мытье. \r\n\r\nМожет разбавляться водой в соотношении 10:1\r\n', 'мягкая вода, лаурет сульфат натрия, диэтаноламин кокамид, сополимер акрилата масло энотеры двухлетней (энотеры), полиэтиленгликоль-3, дистеарат, ароматизирующее вещество, ДМДМ гидантоин, лодопропинил бутилкарбамат, лимонная кислота, хлорид натрия', 1, 1, 1, NULL, 10),
(3, 1, '№ 16 White Coat Evening Primrose Oil Shampoo', 'Шампунь № 16 Масло Вечерней Примулы Для Белой Шерсти', '16-white-coat-evening-primrose-oil-shampoo', '№16', 'Предназначен для белой шерсти, но также может использоваться\r\nна кремовой. Подходит для ежедневного использования.\r\nШампунь для белой шерсти отличается от других подобных \r\nотбеливающих шампуней тем, что не содержит отбеливателей \r\nи вредных химических веществ, которые могут нанести вред шерсти \r\nВашей собаки. Шампунь придает  шерсти  невероятно  белый   цвет.\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n', '', 'Шампунь для белой шерсти, отличается от других подобных отбеливающих шампуней, тем, что не содержит отбеливателей и вредных химических веществ, которые могут нанести вред шерсти Вашей собаки. Основа его схожа с №10 (1%). Нам удалось добиться смешивания пигмента с маслом. Поэтому когда шерсть впитывает масло, она впитывает вместе с маслом пигмент присутствующий в масле. Синий пигмент преломляет яркий свет, придавая шерсти невероятно яркий белый цвет. Прекрасное свойство этого шампуня заключается в том, что Вам не придется беспокоиться об отбеливании других цветов шерсти. Можно мыть вашу собаку хоть каждый день этим шампунем, не нанося никакого ущерба шерсти. Вы только заметите, как шерсть будет становиться все лучше и лучше. ', 'Хорошо намочите шерсть, нанесите шампунь, хорошо пропитайте помогая руками тщательно распределиться по всей шерсти. Оставьте шампунь на 2-5 минут, а затем ее необходимо основательно смыть. Используйте кондиционер Formula №50 или №51, затем промойте и просушите. Если шерсть Вашей собаки очень грязная, то может потребоваться повторное мытье. \r\nМожет разбавляться водой в соотношении 10:1\r\n', 'мягкая вода, лаурет сульфат натрия, диэтаноламин кокамид, сополимер акрилата масло энотеры двухлетней (энотеры), полиэтиленгликоль-3, дистеарат, ароматизирующее вещество, ДМДМ гидантоин, лодопропинил бутилкарбамат, лимонная кислота, хлорид натрия', 1, 1, 1, NULL, 15),
(4, 1, '№ 17 Copper Coat Evening Primrose Oil Shampoo', 'Шампунь № 17 Масло Вечерней Примулы Для Медной Шерсти', '17-copper-coat-evening-primrose-oil-shampoo', '№17', '', '', 'Данный продукт базируется на Formula №10 (1%). Этот шампунь для коричневой шерсти всех оттенков отличается от других продуктов для усиления цвета тем, что не содержит красители или хну. Шерсть, пропитываясь маслом будет впитывать пигмент. Коричневый пигмент будет создавать оптический эффект преломляя все оттенки коричневого цвета, усиливая, делая богаче и ярче. Прекрасное свойство этого шампуня заключается в том, что вам не стоит беспокоиться о потере других цветов шерсти. Этот продукт прекрасно показал себя на трехцветном Английском Кокер Спаниеле, а также на Мягкошерстном Пшеничном Терьере. Этим шампунем можно мыть вашу собаку хоть каждый день, совершенно не боясь причинить вреда шерсти. С каждым днем шерсть будет становиться только лучше и лучше.', 'Хорошо намочите шерсть, нанесите шампунь, хорошо пропитайте помогая руками тщательно распределиться по всей шерсти. Оставьте шампунь на 2-5 минут, а затем ее необходимо основательно смыть. Используйте кондиционер Formula №50 или №51, затем промойте и просушите. Если шерсть Вашей собаки очень грязная, то может потребоваться повторное мытье. Может разбавляться водой в соотношении 10:1', 'мягкая вода, лаурет сульфат натрия, диэтаноламин кокамид, сополимер акрилата, полиэтиленгликоль-3 дистеарат, масло энотеры двухлетней (энотеры), ароматизирующее вещество, ДМДМ гидантоин, лодопропинил бутилкарбамат, лимонная кислота, хлорид натрия, бензофенон-4, синий краситель №1, красный краситель № 33', 1, 1, 1, NULL, 20),
(5, 1, '№ 18 Black Coat Evening Primrose Oil Shampoo', 'Шампунь № 18 Масло Вечерней Примулы Для Черной Шерсти', '18-black-coat-evening-primrose-oil-shampoo', '№18', 'Предназначен для черной или очень темной шерсти, таких окрасов\r\nкак темный красновато-коричневый или махагоновый. Содержит 1%\r\nмасла Вечерней Примулы. Прекрасное свойство этого шампуня \r\nзаключается в том, что вам не стоит беспокоиться о потере других \r\nцветов шерсти. Подходит для ежедневного мытья Вашей собаки.\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n', '', 'Данный продукт базируется на формуле шампуня №10. Шампунь для черной шерсти всех оттенков отличается от других продуктов для усиления цвета тем, что не содержит красители или хну. Шерсть, пропитываясь маслом будет впитывать пигмент. Черный цветной пигмент будет создавать оптический эффект преломляя все оттенки черного цвета, усиливая и делая богаче и ярче. Прекрасное свойство этого шампуня заключается в том, что вам не стоит беспокоиться о потере других цветов шерсти. Данный продукт прекрасно работает на шерсти черных и всех темных расцветок, таких как Ирландский Сеттер или Руби Кинг Чарльз Спаниель (черный пигмент придаст шерсти этих пород богатые цвета красного дерева). Этим шампунем можно мыть вашу собаку хоть каждый день, совершенно не боясь причинить вреда шерсти. С каждым днем шерсть будет становиться только лучше и лучше.', 'Хорошо намочите шерсть, нанесите шампунь, хорошо пропитайте помогая руками тщательно распределиться по всей шерсти. Оставьте шампунь на 2-5 минут, а затем ее необходимо основательно смыть. Используйте кондиционер Formula №50 или №51, затем промойте и просушите. Если шерсть Вашей собаки очень грязная, то может потребоваться повторное мытье. \r\nМожет разбавляться водой в соотношении 10:1\r\n', 'мягкая вода, лаурет сульфат натрия, диэтаноламин кокамид, сополимер акрилата, полиэтиленгликоль-3 дистеарат, масло энотеры двухлетней (энотеры), ароматизирующее вещество, ДМДМ гидантоин, лодопропинил бутилкарбамат, лимонная кислота, хлорид натрия, бензофенон-4, синий краситель №1, красный краситель № 40, жёлтый краситель № 6, кислотный синий № 74\r\n', 1, 1, 1, NULL, 25),
(6, 1, '№ 20 Royal Jelly Shampoo', 'Шампунь № 20 Королевское Желе', '20-royal-jelly-shampoo', '№20', 'Способствует росту волос. Кроме того добавляет объем существующей\r\nшерсти. Особенно рекомендуется собакам во время сезонной линьки,\r\nпосле ложной беременности или родов. Стимулирует рост волос, \r\nпри регулярном использовании. Содержит комплекс белков, жиров и витаминов, которые обеспечивают регенерацию для более густой шерсти.', '', 'Маточное молочко является питательным веществом, которым рабочие пчелы кормят королеву пчел. Таким образом, он содержит не только натуральные жиры, но и целый ряд чистых белков. Эти белки попадают в слой эпидермиса корней волос, добавляя объем текстуру. Этот шампунь великолепно подходит для сук потерявших шерсть после родов, и собакам после линьки. Этот шампунь можно комбинировать с шампунями усиливающими окраску (№ 16, № 17 и № 18), для того чтобы увеличением объема и усилить цвета. ', 'Хорошо намочите шерсть, нанесите шампунь, хорошо пропитайте помогая руками тщательно распределиться по всей шерсти. Оставьте шампунь на 2-5 минут, а затем ее необходимо основательно смыть. Используйте кондиционер Formula №50 или №51, затем промойте и просушите. Если шерсть Вашей собаки очень грязная, то может потребоваться повторное мытье.\r\n\r\nМожет разбавляться водой в соотношении 10:1\r\n', 'мягкая вода, лаурет сульфат натрия, диэтаноламин кокамид, кокамидопропилбетаин, полиэтиленгликоль-3 дистеаратб пчелиное молочко, полисорбат 20, ароматизирующее вещество, ДМДМ гидантоин, лодопропинил бутилкарбамат, лимонная кислота, хлорид натрия, карамельный краситель.', 1, 1, 1, NULL, 30),
(7, 1, '№ 33 Coarse Coat Shampoo ', '№ 33 Шампунь Для Жесткой Шерсти', '33-coarse-coat-shampoo', '№33', 'Идеально подходит для собак с жесткой и грубой шерстью, таких как терьеры,\r\nа так же собакам, которым необходимо сделать шерсть текстурной, например, Колли и\r\nЧау Чау. Основан на экстракте морских водорослей, которые будут увлажнять\r\nшерсть, при том не смягчая структуру.', '', 'Этот шампунь идеально подходит для собак с жесткой или грубой структурой шерсти, таких как терьеры, а так же собакам, которым необходимо поднять шерсть, например, Колли и Чау Чау. Основан на экстракте морских водорослей, которые будут увлажнять шерсть, при этом не смягчая текстуру.', 'Хорошо намочите шерсть, нанесите шампунь, хорошо пропитайте помогая руками тщательно распределиться по всей шерсти. Оставьте шампунь на 2-5 минут, а затем ее необходимо основательно смыть. Используйте кондиционер Formula №50 или №51, затем промойте и просушите. Если шерсть Вашей собаки очень грязная, то может потребоваться повторное мытье.\r\n\r\nМожет разбавляться водой в соотношении 10:1\r\n', 'Вода, лаурет сульфат натрия, диэтаноламин кокамид, кокамидопропилбетаин, полиэтиленгликоль-3 дистеарат, полисорбат 20, ароматизирующее вещество, экстракт из морских водорослей, ДМДМ гидантоин,лодопропинил бутилкарбамат, лимонная кислота, хлорид натрия, голубой краситель №1, жёлтый краситель № 5', 1, 1, 1, NULL, 40),
(8, 1, '№32 Intensely Nourishing Gloss Shampoo', '№32 Шампунь Для Блеска', '32-intensely-nourishing-gloss-shampoo', '№32', 'Содержит высокую концентрацию успокоительных для кожи эфирных масел,\r\nв числе которых Итальянский Бергамот и Мадагаскарская Розовая\r\nГерань. В результате Вы сможете получить невероятную мягкость и блеск.\r\n', '', 'Для создания этого уникального травяного  Gloss шампуня мы использовали лепестки гибискуса sabdariffa,  собранного зимой. Этот метод заготовки гарантирует, что наша формула обеспечит максимальный блеск и натуральность. Высокая концентрация терапевтически успокоительных для кожи эфирных масел, в числе которых Итальянский Бергамот и Мадагаскарская Розовая Герань позволит Вам расширить горизонт достижений в уходе за шерстью вашей собаки. В результате вы получите невероятную мягкость и блеск, а в дополнение приятный ритуал связывающий вас и вашу собаку.', 'Тщательно промочить. Шампунь втереть в волосы и кожу. Для увеличения намыливания, добавить немного воды. Смыть полностью. Весьма рекомендуется для здоровья и красоты вашей собаки применять с питательной маской № 52.', 'вода, децил глюкозид, лауроамфоацетат натрия, метил кокоил таурат натрия, экстракт гибискуса, пшеничные аминокислоты, гидролизованные протеины пшеницы и линолевая кислота, гиалуроновая кислота, сорбит, масло пшеницы обыкновенной (Зародышей пшеницы), масло китайского букуса (жожоба), пантенол, токоферол, дрожжевой экстракт, Ди (лаурил диметил аммоний гидроксипропил) децил глюкозид, лимонная кислота, бегенамидопропил гидроксиэтил димониум хлорид, полиэтиленгликоль-150 пентаэритритил тетранитрат, глюконолактон, бензоат натрия', 1, 1, 1, NULL, 35),
(9, 2, '№ 50 Light Management Conditioner', '№ 50 Кондиционер Легкого Действия', '50-light-management-conditioner', '№50', 'Этот кондиционер содержит продукты кокоса, которые не утяжеляют \r\nшерсть. Удерживает масла и влагу после мытья шампунем.\r\nОн придаст шерсти Вашей собаки блеск и здоровый вид. ', '', 'Этот кондиционер содержит продукты кокоса, которые&nbsp;придадут шерсти вашей собаки блеск и здоровый вид.&nbsp;Сохраняет объем. Кондиционер закрывает слой кутикулы волосяного покрова, удерживая масла и влагу, попавшие из шампуней Isle Of Dogs на шерсть. Разводите кондиционер в соотношении 10:1 с водой или больше в зависимости от желательной жесткости шерсти.', 'Нанесите достаточное количество кондиционера на шерсть и кожу, подождите 5 минут, после чего тщательно промойте. Просушите шерсть. \r\n\r\nМожет разбавляться водой в соотношении 10:1\r\n\r\nСОВЕТ: Если вы хотите, чтобы шерсть росла лучше, подержите кондиционер на шерсти от 15 до 20 минут перед полосканием. Это позволит кондиционеру полностью пропитать кожу и шерсть. Делайте это регулярно в течении месяца результаты Вас удивят. \r\n', 'вода, цетиариловый спирт, полиэтиленгликоль-40 касторовое масло, стеаралкониум хлорид, цетиловый спирт, бегентримониум-метосульфат, масло энотеры двухлетней (энотеры), ДМДМ гидантоин, лодопропинил бутилкарбамат, ароматизирующее вещество, красный краситель № 33', 1, 1, 1, NULL, 100),
(10, 2, '№ 51 Heavy Management Conditioner', '№ 51 Кондиционер Сильного Действия', '51-heavy-management-conditioner', '№51', 'Делает шерсть более гладкой. Идеально подходит для сухой и тусклой\r\nшерсти, породам с двойной шерстью, а также для пород с ниспадающей\r\nшерстью.', '', 'Этот кондиционер немного тяжелее, чем другие наши легкие кондиционеры. Она закрывает слой кутикулы волосяного покрытия, делая шерсть более гладкой. Идеально подходит для сухой и тусклой шерсти, породам с двойной шерстью, а также для ниспадающей шерсти, которая должна свисать вниз. ', 'Нанесите достаточное количество кондиционера на шерсть и кожу, подождите 5 минут, после чего тщательно промойте. Просушите шерсть.\r\n\r\nМожет разбавляться водой в соотношении 10:1\r\n\r\nСОВЕТ: Если вы хотите, чтобы шерсть росла лучше, подержите кондиционер на шерсти от 15 до 20 минут перед полосканием. Это позволит кондиционеру полностью пропитать кожу и шерсть. Делайте это регулярно в течении месяца и результаты Вас удивят.\r\n', 'вода, цетиариловый спирт, полиэтиленгликоль-40 касторовое масло, стеаралкониум хлорид, бегентримониум-метосульфат, пантенол, сок листа алоэ-вера, экстракт листа розмарина лекарственного (розмарина), экстракт листа лаванды узколистной (лаванды), масло энотеры двухлетней (энотеры), ДМДМ гидантоин, лодопропинил бутилкарбамат, ароматизирующее вещество, голубой краситель №1, жёлтый краситель № 5', 1, 1, 1, NULL, 110),
(11, 2, '№52 Intensely Nourishing Nutrient Masque', '№52 Питательная Маска', '52-intensely-nourishing-nutrient-masque', '№52', 'Основное питание для волос, которое может применяться в качестве\r\nфинишной обработки после каждого купания Вашей собаки.\r\nМаска высокой концентрации и глубокого кондиционирования.\r\n', '', 'Высокой концентрации, исключительно глубокого кондиционирования восстанавливаюший структуру и прочность всех типов волос, уменьшает ломкость и выпадение, а также эффективно предотвращает спутывание. Пшеничный протеин и витамины, богатые жирными кислотами придают природную красоту, всецело укрепляя здоровье волос. Основное питание для волос, которое должно применяться в качестве финишной обработки после каждого купания, даже если ваша собака имеет хорошее состояние шерсти.', 'После мытья шампунем нанести большое количество Nutrient Masque на шерсть. Глубоко втереть в шерсть и оставить на 3-5 минут. Смыть полностью.\r\n\r\n', 'вода, бегентримониум-метосульфат (рапсовое масло) и цетиловый спирт (без ГМО продуктов), гидролизованные протеины пшеницы и олигосахарид пшеницы, пантенол (витамин В5) и гидролизованные протеины пшеницы и линолевая кислота, гиалуроновая кислота (растительного происхождения), сорбит (органичный увлажнитель), масло пшеницы обыкновенной (Зародышей пшеницы), масло китайского букуса (жожоба), токоферол (витамин Е), дегидрацетовая кислота, бензиловый спирт', 1, 1, 1, NULL, 120),
(12, 3, '№ 62 Evening Primrose Oil Conditioning Mist', '№ 62 Масло Вечерней Примулы Спрей-Кондиционер', '62-evening-primrose-oil-conditioning-mist', '№62', 'Обладает легким ароматом. Отлично подходит для расчесывания в \r\nпериод между купаниями или между выставками. Успокаивает и увлажняет\r\nкожу, придает блеск любой шерсти. Пользуется большой популярностью у\r\nзаводчиков.', '', 'Кондиционирующий спрей содержащий масло Вечерней Примулы и растительные экстракты, которые позволяют успокоить кожу, предотвратить шелушение кожи и придать блеск любой шерсти. Этот продукт, увлажняет лучше, чем №63 и обладает более легким ароматом. Он отлично подходит для расчесывания в период между купаниями или между выставками, а так же пользуется большой популярностью среди заводчиков. ', 'Перед применением необходимо хорошо взболтать. Слегка нанесите спрей на шерсть и щетку. Беречь от попадания в глаза собаки. Незаменим при устранении неприятных запахов и прекрасно помогает при расчесывании между купаниями.', 'вода, СД спирт 40-Б, полиэтиленгликоль-12 полидиметилсилоксан, пантенол, полисорбат 20, масло энотеры двухлетней (энотеры), экстракт листа розмарина лекарственного (розмарина), экстракт цветков ромашки лекарственной экстракт тимьяна обыкновенного (тимьяна), ароматизированное вещество.', 1, 1, 1, NULL, 200),
(13, 3, '№ 63 Detangle Conditioning Mist', '№ 63 Распутывающий Спрей-Кондиционер', '63-detangle-conditioning-mist', '№63', 'Идеально подходит для собак с длинной шерстью и шерстью средней\r\nдлины. Помогает распутать шерсть и поддерживать ее в аккуратном состоянии.\r\n', '', 'Идеально подходит для собак с длинной шерстью и шерстью средней длины. Помогает распутывать шерсть и поддерживает ее в аккуратном и чистом состоянии. Остается на шерсти, чтобы предотвратить запутывание и образование колтунов, кроме того, не будет склеивать ее.', 'Сбрызните запутанную или свалявшуюся область шерсти как минимум за две минуты до расчесывания. Не оставляет остатков на шерсти. Используйте так часто, как это необходимо. Избегайте попадания в глаза собаки.', 'мягкая вода, лаурет сульфат натрия, диэтаноламин кокамид, сополимер акрилата, полиэтиленгликоль-3 дистеарат, масло энотеры двухлетней (энотеры), ароматизирующее вещество, ДМДМ гидантоин, лодопропинил бутилкарбамат, лимонная кислота, хлорид натрия', 1, 1, 1, NULL, 210),
(14, 5, 'Isle Hold ', 'Пенка Для Фиксации', 'isle-hold', 'hold', 'Кондиционирующая пенка мусс или гель для укладки без каких-либо \r\nаэрозолей. Богатая питательным соевым белком, обеспечивает \r\nневероятный объем и легкую укладку. Не сушит шерсть, но добавляет \r\nблеск.', '', 'Кондиционирующая пенка мусс или гель для укладки без каких либо аэрозолей, . Богатая питательным соевым белком, обеспечивает невероятный объем и облегчающий контроль за укладкой волос, не оставляя липкого остатка на шерсти. Она не сушит шерсть, но добавляет блеск. Прекрасно работает поверх Isle Thicken для создания максимального обьема.', 'Выдавите на ладони пенку двумя-тремя нажатиями. Нанесите на нужные участки увлажненной шерсти, после чего просушите и уложите так, кА Вам это необходимо.\r\n\r\nПодсказка: Нанесите на короткую шерсть ног Вашей собаки и придайте им вид и выразительную форму. Так же прекрасно работает на голове, щеках и других частях тела. \r\n', 'вода, полисорбат 20, СД спирт 40-Б, поликватерниум-11, поливинилпирролидон,\r\nкокамидопропилбетаин, пантенол, протеин глицина сои (соевых бобов), цианокобаламин (витамин В12), масло семян китайского букуса (жожоба), диэтаноламин кокамид, полиэтиленгликоль-40 касторовое масло, масло сладкого (Испанского) апельсина, пропилен гликоль, диазолидинил мочевина, метилпарабен, пропилпарабен, ароматизирующее вещество.\r\n', 1, 1, 2, NULL, 805),
(15, 5, 'Isle Lift', 'Спрей Для Объема', 'isle-lift', 'lift', 'Придает структуру от корней волос, обеспечивает дополнительный \r\nобъем и поднимает шерсть. Рекомендован для таких пород, как Пудель,\r\nПекинес, Терьер, Колли и Шелти. Прекрасно работает для фиксации\r\nшерсти на холке для придания красивой линии верха.', '', 'Без аэрозольный, богатый белком спрей придает структуру от корней волос, обеспечивает дополнительный объем и поднимает шерсть.\r\n\r\nЭтот продукт идеально подходит для пород, которые нуждаются в поднятой шерсти, такие как Пекинес, Пудель, Терьер, Шелти и Колли. Прекрасно работает для фиксации шерсти на холке для придания красивой линии верха. Прекрасное свойство этого продукта в том, что он наносится на корни волос и так же как и все наши другие продукты не оставляет ощущения липкости на ощупь. Так же, как Hold и Isle Style производится в экономичном исполнении.\r\n', 'Спрей наносится на корни и подшерсток, после чего сушится и придается форма как обычно. Может быть нанесено на руки, а затем на нужные участки головы и ног для придания формы.', 'вода, СД спирт 40-Б, ВП/ВА сополимер, поливинилпирролидон, полиэтиленгликоль-40  гидрогенизированное  касторовое масло, протеин глицина сои (соевых бобов), пантенол, цианокобаламин (витамин В12), масло семян китайского букуса (жожоба), полиэтиленгликоль-12 полидиметилсилоксан, медь PCA, экстракт глицина семян сои (соевых бобов), экстракт листа алоэ-вера, хлорид четвертичной соли аммония, ацетат токоферола, ароматизирующее вещество', 1, 1, 2, NULL, 840),
(16, 5, 'Isle Repair', 'Восстанавливающий Кондиционер', 'isle-repair', 'repair', 'Великолепный кондиционер для ниспадающей шерсти является\r\nфантастическим продуктом для завершающей отделки, устранения \r\nстарых и поврежденных волос, снятия статического электричества.', '', 'Великолепный кондиционер для ниспадающей шерсти может быть разбавлен в бутылке для спрея и является фантастическим продуктом для завершающей отделки, устранения старых и поврежденных волос , а так же в качестве сильного антистатического средства. Продолжительное использование помогает исцелить сухие и поврежденные волосы, усиливая блеск с течением времени. Заводчики Английского Сеттера пришли к выводу, что распыление Isle Repair в местах попадания мочи на шерсть помогает защитить от окрашивания и предотвратить порчу.', 'Для поврежденной шерсти мы рекомендуем применение продукта без разбавления нанеся на влажную шерсть перед укладкой и сушкой. За день шоу, разбавив 1-2 столовые ложки средства на 250 милилитров воды в бутылке для спрея, нанесите на шерсть и хорошо просушите. Коэффициент разбавления может быть изменен для достижения наилучшего эффекта.\r\n\r\nНебольшая подсказка: Когда будете разбавлять, то начните с небольшого количества воды, примерно до первого деления, налив в бутылку спрей, после чего добавьте необходимое количество продукта, хорошо взболтайте, а затем добавьте оставшуюся воду. \r\n', 'вода, гидроксиэтилцеллюлоза, амодиметикон, октоксинол-40, изолаурет 6, хлорид четвертичной соли аммония, пантенол, экстракт цветков ромашки лекарственной, экстракт грейпфрута, экстракт плодов шиповника собачьего, экстракт тысячелистника обыкновенного, эмульгирующийся воск НФ, гидроксипропил-тримоний-хлорид гуар, ДМДМ гидантоин, метил глюцет-20, метилпарабен, пропилен гликоль, ароматизированное вещество/парфюм, пропилпарабен, лимонная кислота, бензофенон-4, красный краситель № 40, оранжевый краситель № 4.', 1, 1, 2, NULL, 860),
(17, 5, 'Isle Revive', 'Регенерирующий Кондиционер', 'isle-revive', 'revive', 'Данный продукт был специально разработан для усиления эффекта \r\nкудрявых и волнистых волос, также делает более эластичными прямые \r\nволосы, не распрямляя их. Прекрасно помогает в ситуации, когда нет \r\nвремени и в последний момент надо сделать неотразимый вид шерсти.', '', 'Данный продукт был специально разработан для усиления эффекта кудрявых и волнистых волос, также делает более эластичными прямые волосы, не распрямляя их. Мы еще его называем мгновенным ликвидатором тусклости, сухости и безжизненности волос, как будто вдруг пришла "Весна" и шерсть преобразилась моментально. Прекрасно помогает в ситуации, когда нету времени и в последний момент надо сделать неотразимый вид шерсти.', 'Для использования его в качестве аэрозоли, разбавить 1-2 столовые ложки средства на 250 мл. воды в бутылке для спрея, нанести на шерсть и уложить расческой используя низкотемпературный фен и средние скорости воздушного потока. Может быть использован для максимального эффекта на влажной шерсти, нанесите руками и хорошо распределите, после чего уложите как обычно.\r\n\r\nНебольшая подсказка: Когда будете разбавлять, то начните с небольшого количества воды, примерно до первого деления, налив в бутылку спрей, после чего добавьте необходимое количество продукта, хорошо взболтайте, а затем добавьте оставшуюся воду. \r\n', 'мягкая вода, винил капролактам / поливинилпирролидон / диметиламиноэтил метакрилат сополимер, глицерин, пантенол, пантенил гипрокспропил, дистеарилдимоний хлорид, гидролизованный протеин риса, экстракт лопуха большого (лопуха), экстракт плюща обыкновенного (плюща), экстракт пажитника сенного (пажитника), миристиновокислый изопропил, бутилен гликоль, экстракт красных водорослей, триэтаноламин, акрилаты/С10-30 алкил акрилат кроссполимер, ноноксинол-10, твёрдый тримониум хлорид, амодинэтикон, лодопропинил бутилкарбамат, ДМДМ гидантоин, бензофенон-4, двунатриевая этилендиаминтетрауксусная кислота, синий краситель №1', 1, 1, 2, NULL, 890),
(18, 5, 'Isle Straighten ', 'Разглаживающий Кондиционер', 'isle-straighten', 'straighten ', 'Разработан специально для пород, которым требуется приглаженная шерсть\r\nи необходимо сохранить шелковитость шерсти. Этот продукт содержит экстракт\r\nкоры березы, что помогает выпрямить и разгладить волосы и в то же время \r\nзащищает шерсть от термоповреждений. Также может использоваться\r\nдля распутывания шерсти и снятия статики. ', '', 'Этот продукт содержит экстракт коры березы, что помогает выпрямить и разгладить волосы и в то же время защищает шерсть от термо повреждений. Если вам нужно сделать гладкую, завивающуюся шерсть, слегка нанесите средство на щетку и разгладьте ей шерсть вниз. Это придаст невероятный блеск шерсти. Также может использоваться в качестве распутывающего спрея для спутанной шерсти(для этого наносить продукт в таких количествах, сколько это необходимо на сухую шерсть, расчесать расческой колтуны и запутанные волосы, завершить купанием).', 'Удерживая на расстоянии 25 см. слегка нанесите на влажную шерсть. Сушить и укладывать как обычно. Использовать умеренно, и не торопиться. ', 'циклометикон, изопропил пальминат, диметилконол, октил метоксициннамат, экстракт берёзовой коры, экстракт лимонника, масло жожоба, ацетат токоферола, ароматизирующее вещество', 1, 1, 2, NULL, 850),
(19, 3, 'Isle Strengthen', 'Укрепляющий Спрей-Кондиционер', 'isle-strengthen', 'strengthen', 'Кондиционер спрей, не требующий смывания для длинношерстных пород, \r\nпредотвращает ломкость и повышает гибкость, уменьшает вред от\r\nпостоянного расчесывания. Придает блеск без утяжеления, облегчает \r\nраспутывание волос. Может использоваться ежедневно.', '', 'Кондиционер спрей, не требующий смывания для длинношерстных пород, предотвращает ломкость и повышает гибкость, уменьшает вред от постоянного расчесывания и укладки помогая шерсти расти более длинной и крепкой.\r\n\r\nСодержит белки, которые увеличивают прочность волос, а компоненты кондиционирования увлажняют их. Этот не требующий смывания кондиционер может использоваться ежедневно, перед расчесыванием в период между купаниями.\r\n', 'Нанести спрей на мокрую или сухую шерсть перед расчесыванием. Может быть также разбавлен в пропорции 2:1 с водой и использоваться для повторного увлажнения. ', 'вода, хлорид четвертичной соли аммония, мёд гидроксипропилтримониум, поликватерниум-11, гидролизованный шёлк, амодиметикон, аминокислоты шёлка, поликватерниум-4, экстракт хвоща полевого, сок листа алоэ-вера, экстракт листа ромашки медицинской, этилэксил метоксициннамат фитантриол, ацетат токоферола, аскорбиновая кислота, тридецет-12, ароматизирующее вещество/парфюм, C11-15 парет-7, C12-16 парет-9, глицерин, полисорбат-20, метилхлороизотиазолинон, метилизотиазолинон, синий краситель №1, жёлтый краситель № 5', 1, 1, 2, NULL, 870),
(20, 5, 'Isle Style', 'Айл Стайл', 'isle-style', 'style', 'Спрей, который сделает шерсть абсолютно невесомой. Он был специально\r\nразработан для подготовки шерсти к стрижке или для придания шерсти \r\nнемного дополнительной структуры. Идеально подходит для подготовки шерсти\r\nк стрижке ножницами и укладке. Также подходит для быстрого набивания шерсти,\r\nиспользуется как основа для пудры. Рекомендуется для закрепления волос\r\nпород с ниспадающей шерстью и для укладки волос на голове терьеров.', '', 'Спрей, который сделает шерсть абсолютно невесомой. Он был специально разработан для подготовки шерсти к стрижке или для придания шерсти немного дополнительной текстуры. Этот продукт не будет пачкать и не испортит Ваши ножниц, а шерсть не станет липкой на ощупь. Этот продукт идеально подходит для шерсти таких пород, как Пудель, Португальская Водянае собака и Бишон-фризе, и дает фантастические результаты на ногах шнауцеров и Керри Блю терьеров. Идеально подходит для подготовки шерсти к стрижке ножницами и укладке.', 'Распылить на влажную или сухую шерсть с расстояния 15-20см. и тщательно расчесать щеткой или гребнем. Может быть использована как на влажной, так и на сухой шерсти.\r\n\r\nСовет: Попробуйте использовать для набивания шерсти, например распыляя на ноги и расчесывается против шерсти. Так же может как основа для пудры. \r\n', 'СД спирт 40-Б, вода, октилакриламид /акрилаты/сополимер бутиламиноэтил метакрилата, винилацетат/кротонаты/ сополимер винил неодеканоат, кокамидопропилбетаин, полиэтиленгликоль-12 диметикон, протеин глицина сои (соевых бобов), пантенол, цианокобаламин (витамин В12), масло семян китайского букуса (жожоба), аминометил пропанол, полисорбат 20, токоферил ацетат, бензофенон-4, пропиленгликоль, экстракт листьев шалфея, бутиленгликоль, экстракт крапивы, экстракт розмарина, карбомер, ароматизирующее вещество.', 1, 1, 2, NULL, 880),
(21, 5, 'Isle Thicken', 'Кондиционер Для Объема (Усилитель Объема)', 'isle-thicken', 'thicken', 'Данный продукт был разработан для добавления фантастического объема\r\nшерсти. Он попадает в эпидермис и каждый отдельный волос, придавая\r\nневероятный объем.', '', 'Данный продукт был разработан для добавления фантастического объема шерсти. Он попадает в эпидермис и каждый отдельный волос, придавая шерсти невероятно большой объем, при этом шерсть не будет иметь ощущения присутствия каких либо косметических средств. Он может быть использован в чистом виде или разбавленным как спрей.', 'Если используется в чистом виде, то лучше всего наносить на влажную шерсть и использовать в небольшом количестве. При разбавлении пропорция зависит от типа шерсти и может быть где-то от 1/2 до 2 столовых ложек разбавленными с 250мл воды в бутылке распылителе. Распылить на шерстяной покров и высушить воздухом.\r\n\r\nНебольшая подсказка: Когда будете разбавлять, то начните с небольшого количества воды, примерно до первого деления, налив в бутылку спрей, после чего добавьте необходимое количество продукта, хорошо взболтайте, а затем добавьте оставшуюся воду.\r\nДополнительно: Используется для придания вида мощного костяка на ногах. \r\n', 'вода, глицерин, глицерил стеарат, цетиловый спирт, VP/VA сополимер, глицерин, стеаралкониум хлорид, полиэтиленгликоль-150 дистеарат, полиэтиленгликоль-100 стеарат, центримониум хлорид, гликоль стеарат, полиэтиленгликоль-12 полидиметилсилоксан, метилпарабен, дицетилдимониум хлорид, ароматизирующее вещество/парфюм, ДМДМ гидантоин, аминокислоты пшеницы, пропилпарабен, красный краситель № 40, оранжевый краситель № 4', 1, 1, 2, NULL, 800),
(22, 4, '№ 91 Royal Jelly Coat Supplement', '№ 91 Добавка Для Шерсти Королевское Желе', '91-royal-jelly-coat-supplement', '№91', 'Эта добавка содержит маточное молочко. Его белки способствуют\r\nросту волос и снижают вероятность их потери. ISLE OF DOGS рекомендует\r\nдобавлять в рацион эту добавку ежедневно, для поддержания роста волос.', '', 'Эта добавка для шерсти содержит маточное молочко, взвешенное в растительных маслах. Белки, содержащиеся в маточном молочке способствуют росту волос и снижают вероятность их потери.', 'Полное руководство по применению, дозам в соотношении к размерам питомца можно прочитать на бутылке.', 'масло канолы, пчелиное молочко, этилпарабен, метилпарабен, пропилпарабен', 1, 1, 1, NULL, 300),
(23, 4, '№ 92 EFA Blend Coat Supplement', '№ 92 Добавка Для Шерсти Основные Жирные Кислоты', '92-efa-blend-coat-supplement', '№92', 'Добавка на основе масла Вечерней Примулы обладает успокаивающим \r\nдействием. Особенно эффективна при шелушении и зуде кожи.\r\nБогатая жирными кислотами является крайне важной для здоровья кожи, \r\nблеска шерсти, и хорошим регулятором необходимых масел в шерсти.\r\n', '', 'Добавка на основе масла Вечерней примулы особенно эффективна при шелушении кожи, зуде кожи и обладает природной, успокаивающей способностью. Богатая жирными кислотами является крайне важной для здоровья кожи, блеска шерсти, и хорошим регулятором необходимых масел в шерсти. Предназначен для использования в сочетании непосредственно с продуктами ISLE OF DOGS. Перед применением необходимо хорошо взболтать. ', 'Полное руководство по применению, дозам в соотношении к размерам питомца можно прочитать на бутылке.', 'масло канолы, линолевая кислота, этилфарабен, метилпарабен, пропилпарабен', 1, 1, 1, NULL, 310),
(24, 1, 'Sit Still Shampoo', 'Шампунь Для Нежной Очистки и Увлажнения', 'sit-still-shampoo', 'sit-still', 'Создан для мягкого очищения и увлажнения, делает шерсть гладкой и послушной. Идеально подходит для укладки с эффектом мокрых” волос. Увлажняет шерсть, делая ее сильной и повышая ее эластичность, что защищает шерсть от повреждений. Также подходит для сухой или поврежденной шерсти любого типа.', '', 'Данный продукт относится к разряду салонной косметики. Разработан специально для груминг салонов, профессиональных грумеров и крупных питомников. Предназначен для нежной очистки и увлажнения, оставляя ощущение мягкости и гладкости шерсти. Добавляет увлажняющие свойства, способствует укреплению волос и увеличивает эластичность для предотвращения повреждений покрова. Кроме того, хорошо работает на сухой и поврежденной шерсти всех типов.', 'Разбавить с водой в соотношении до 20:1. Смочить шерсть, нанести шампунь, тщательно втереть и тщательно промыть. При необходимости повторить. Для особо загрязненной шерсти лучше предварительно искупать используя Шампунь «Stay Clean», после чего искупать используя данный продукт.', 'Вода, лаурет сульфат аммония, лаурил сульфат аммония, кокамидопропилбетаин, лаурет сульфат соды, полиэтиленгликоль-3 дистеарат, глюкозид кокоса, глицерил олеат, глицерин, пантено, пропиленгликоль, экстракт алоэ-вера, ДМДМ гидантоин, иодопропинил битилкарбамат, полисорбат 20, лимонная кислота, хлорид соды, ароматическое вещество.', 1, 1, 3, NULL, 115),
(25, 1, 'Stand Up Shampoo', 'Шампунь Для Создания Текстуры и Объема', 'stand-up-shampoo', 'stand-up', 'Прекрасное средство, придающее объем и плотность мягкой шерсти, что облегчает укладку и стрижку. Также помогает сохранить свойства и качества жесткой шерсти.', '', 'Данный продукт относится к разряду салонной косметики. Разработан специально для груминг салонов, профессиональных грумеров и крупных питомников. Превосходный моющий продукт, который добавляет тектуру и объем мягкому типу шерсти и облегчает укладку и стрижку. Сохраняет качество и характер жесткой шерсти. Придаст шерсти больше объема.', 'Разбавить с водой в соотношении до 20:1. Смочить шерсть, нанести шампунь, тщательно втереть и тщательно промыть. Некоторые типы шерсти требуют повторной мойки для достижения наилучшего результата.', 'Вода, лаурет сульфат аммония, кокамид диэтаноламин, кокамидопропилбетаин, поликватерниум-10, пропиленгликоль, экстракт лепестков календулы, экстракт розмарина, эвкалиптовое масло, ДМДМ гидантоин, иодопропинил битилкарбамат, бензофенон-4, полисорбат 20, лимонная кислота, хлорид соды, ароматическое вещество,  желтый №6.', 1, 1, 3, NULL, 120),
(26, 1, 'Stay Clean Shampoo', 'Шампунь «Оставайся Чистым»', 'stay-clean-shampoo', 'stay-clean', 'Деликатный очищающий шампунь, удаляет грязь и запах, надолго сохраняет эффект, позволяя реже купать собаку. Можно применять отдельно или перед использованием любого нашего шампуня. ', '', 'Данный продукт относится к разряду салонной косметики. Разработан специально для груминг салонов, профессиональных грумеров и крупных питомников. Мягкий, но глубокий очистки шампунь для удаления грязи и запаха, а также увеличивает время между купаниями. Хорошо работает сам по себе, а также может быть использован в первую очередь, с последующим применением других шампуней. Является смесью клубники, жасмина и игристого вина.', 'Разбавить с водой в соотношении до 20:1. Смочить шерсть, нанести шампунь, тщательно втереть и тщательно промыть. При необходимости повторить. Для особо загрязненной шерсти лучше предварительно искупать используя "Шампунь Для Создания Текстуры и Объема", после чего искупать используя данный продукт.', 'Вода, лаурет сульфат аммония, кокамидопропилбетаин,\r\nкокамид диэтаноламин, экстракт семян камелии (зеленый чай),\r\nпантенол, экстракт алоэ барбадензис, экстракт гамамелиса, пропиленгликоль, ДМДМ гидантоин, иодопропинил битилкарбамат, лимонная кислота, хлорид соды, ароматическое вещество.\r\n', 1, 1, 3, NULL, 110),
(27, 2, '2 Build Conditioner', 'Омолаживающий Кондиционер На Основе Протеина', '2-build-conditioner', '2build', 'Омолаживающий кондиционер на основе протеина, помогает восстановить шерсть от повреждений, вызванных диетой, механических повреждений. Добавляет объем и делает шерсть более послушной для укладки и стрижки. ', '', 'Данный продукт относится к разряду салонной косметики. Разработан специально для груминг салонов, профессиональных грумеров и крупных питомников. Омолаживающий кондиционер на основе протеина, способствующий восстановлению повреждений вызванных неправильной диетой или парикмахерскими инструментами. Увеличивает объем и облегчает стрижку и укладку. Не смягчает покров настолько, насколько его смягчают другие кондиционеры.', 'Может быть разбавлен с водой до 5:1 или использован в чистом виде. Для достижения натлучших результатов нанести продукт на чистую, влажную шерсть и оставить на 2-3 минуты, после чего смыть.', 'Вода, цетиариловый спирт, полиэтиленгликоль-40 касторовое масло, хлорид стеарилалкония, хлорид бегентримониума, масло энотеры двухлетней, масло семян китайского букуса (жожоба), кератиновые аминокислоты, пантенол, токоферил ацетат, ретинил пальмитат, ДМДМ гидантоин, иодопропинил битилкарбамат, бензофенон-4, ароматическое вещество,  карамель.', 1, 1, 3, NULL, 170),
(28, 2, '2 Heal Conditioner', 'Глубоко Проникающий Кондиционер', '2-heal-conditioner', '2heal', 'Глубоко проникающий кондиционер, созданный для увлажнения, защиты и предотвращения повреждений шерсти, придает блеск. Делает шерсть более мягкой, гладкой и послушной. Постоянное применение придаст шерсти более роскошный вид.', '', 'Данный продукт относится к разряду салонной косметики. Разработан специально для груминг салонов, профессиональных грумеров и крупных питомников. Глубоко проникающий кондиционер, созданный для увлажнения, защиты и предотвращения повреждений шерсти, придает блеск. Делает шерсть более мягкой, гладкой и послушной. Постоянное применение придаст шерсти более роскошный вид.', 'Может быть разбавлен с водой до 5:1 или использован в чистом виде. Для достижения натлучших результатов нанести продукт на чистую, влажную шерсть и оставить на 2-3 минуты, после чего смыть. Может использоваться в сочетании с "Шампунем Для Нежной Очистки и Увлажнения" или наноситься на выбранные части тела, такие как туловище и конечности. Когда необходимо добиться объема на других частях тела используйте в сочетании с "Шампунем Для Создания Текстуры и Объема" и "Омолаживающий Кондиционер На Основе Протеина". ', 'Вода, цетиариловый спирт, полиэтиленгликоль-40 касторовое масло, хлорид стеарилалкония, хлорид бегентримониума, пантенол, полиэтиленгликоль -12 полидиметилсилоксан, экстракт алоэ барбадензис, токоферил ацетат, ретинил пальмитат, ДМДМ гидантоин, иодопропинил битилкарбамат, этилгексил метоксиннамат, ароматическое вещество.\r\n\r\n', 1, 1, 3, NULL, 160),
(29, 3, '4 Control Grooming Spray', 'Спрей Для Контролируемого Ухода', '4-control-grooming-spray', '4control', 'Кондиционер-спрей, помогает избавиться от статического электричества и избавляет шерсть от спутывания, не повреждая ее. Повышает упругость шерсти, что предотвращает ломкость. Придает заметную гладкость длинной шерсти и блеск всем видам шерсти. Отталкивает грязь, позволяя шерсти дольше оставаться чистой. ', '', 'Данный продукт относится к разряду салонной косметики. Разработан специально для груминг салонов, профессиональных грумеров и крупных питомников. Кондиционер-спрей, помогает избавиться от статического электричества и избавляет шерсть от спутывания, не повреждая ее. Повышает упругость шерсти, что предотвращает ломкость. Придает заметную гладкость длинной шерсти и блеск всем видам шерсти. Отталкивает грязь, позволяя шерсти дольше оставаться чистой. Отлично подходит для распыления на шерсть непосредственно перед стрижкой, так как смазывает шерсть.', 'Может наноситься на влажную или сухую шерсть. Использовать в чистом виде или разбавить в соотношении от 2:1 до 3:1 и нанести на шерсть перед стрижкой. Хорошо сочетается с одновременным использованием "Шампуня Для Нежной Очистки и Увлажнения" и "Глубоко Проникающего Кондиционера".', 'Вода, дикокоилетил гидроксиэтилмоний, метосульфат,\r\nпропиленгликоль, пантенол, полисорбат 20, глюкозид кокоса, глицерил олеат, протеин сердцевины овса обыкновенного, экстракт цветов ромашки лекарственной, экстракт розмарина, аминокислоты шелка, гидролизованный протеин пшеницы пропиленгликоль-пропил силанетриол, токоферол ацетат, ретинил пальмитат, тетранатрий ЭДТК, ДМДМ гидантоин, иодопропинил битилкарбамат, ароматическое вещество.\r\n', 1, 1, 3, NULL, 260),
(30, 3, '4 Style Grooming Spray', 'Спрей Для Укладки', '4-style-grooming-spray', '4style', 'Укрепляющий спрей, придающий объем и плотность, что облегчает укладку и стрижку.', '', 'Данный продукт относится к разряду салонной косметики. Разработан специально для груминг салонов, профессиональных грумеров и крупных питомников. Текстурирующий спрей увеличивающий объем и структуру. Облегчает укладку и стрижку. Облегчает стрижку поднимая мягкую шерсть. Помогает предотвратить расслоение кончиков волос. Позволяет получить милую, маленькую и округлую головку у Йоркширстых терьеров и Шиа-Тсу.', 'Может наноситься на влажную или сухую шерсть. Для достижения максимального эффекта использовать в чистом виде или разбавить с водой для достижения меньшего эффекта.Может применяться в сочетании с  "Шампунем Для Создания Текстуры и Объема" и "Омолаживающим Кондиционером На Основе Протеина"', 'Вода, дикокоилетил гидроксиэтилмоний, метосульфат,\r\nпропиленгликоль, пантенол, полисорбат 20, глюкозид кокоса, глицерил олеат, протеин сердцевины овса обыкновенного, экстракт цветов ромашки лекарственной, экстракт розмарина, аминокислоты шелка, гидролизованный протеин пшеницы пропиленгликоль-пропил силанетриол, токоферол ацетат, ретинил пальмитат, тетранатрий ЭДТК, ДМДМ гидантоин, иодопропинил бутилкарбамат, ароматическое вещество. ', 1, 1, 3, NULL, 250),
